// Loop sobre os itens de entrada
for (const item of $input.all()) {
    // Obter o texto de entrada
    const text = item.json.output;

    // Dividir o texto por quebras de linha duplas
    const output = text.split('\n\n').map(message => message.trim());

    // Adicionar o resultado ao JSON do item
    item.json.output = output;
}

// Retornar os itens modificados
return $input.all();
