/**
 * Código unificado para realizar backup no GitHub de:
 *   - Itens do n8n (workflows/credentials) – todos os itens do nó "Convert Files" serão salvos.
 *   - Stacks do Portainer (em YAML).
 *   - Schema do Directus (o conteúdo do nó "Get Schema").
 *
 * Requisitos:
 * - "Get Global Vars" fornece github_username e github_repository.
 * - "n8n Backups" retorna JSON com "stdout" (caminho base para backups).
 * - "Convert Files" contém os itens a serem salvos, inclusive aquele com data.type === 'githubApi'
 *      (token GitHub em data.data.accessToken).
 * - "Get n8n Backups" retorna os backups anteriores dos itens n8n.
 * - "Get Stacks" retorna, para cada stack:
 *      * json.stackName – nome da stack.
 *      * json.file.StackFileContent – conteúdo (YAML) da stack.
 * - "Get Stacks Backups" retorna um array onde cada item possui json.data (backup anterior da stack).
 * - "Get Schema" retorna o schema do Directus em json.data.
 * - "Get Schema Backup" retorna, se existir, o backup anterior do schema em json.data.
 *
 * Lógica:
 *  - Para cada item, o novo conteúdo (após trim) é comparado com o backup (após trim):
 *      * Se forem iguais, nenhuma operação é realizada.
 *      * Se o backup for null, a operação primária é de criação.
 *      * Se o backup existir e for diferente, a operação primária é de atualização.
 *  - Não é feita verificação GET prévia; se a operação primária falhar, é feito um GET para obter o SHA atual e
 *    tenta-se a operação inversa (fallback):
 *       * Se tentou criar, passa a atualizar (obtendo o SHA via GET).
 *       * Se tentou atualizar, passa a criar (removendo o SHA).
 *  - Um delay de 1 segundo é aplicado somente se uma requisição PUT (ou fallback) for efetuada.
 *  - A propriedade "advancedLogs" será incluída nos resultados somente se advancedLogsEnabled for true;
 *    caso contrário, essa chave não será adicionada.
 */

// ----------------------------------------------------------------------
// Configurações Gerais (hardcode)
// ----------------------------------------------------------------------
const advancedLogsEnabled = false; // Altere para true para exibir logs avançados

// ----------------------------------------------------------------------
// Importa o axios para requisições HTTP
const axios = require('axios');

// Função de delay (aguarda ms milissegundos)
const delay = ms => new Promise(resolve => setTimeout(resolve, ms));

// ===================================================================
// 1. Configurações Comuns
// ===================================================================

// Obtém as variáveis globais do GitHub a partir do nó "Get Global Vars"
const globalVars = $('Get Global Vars').first().json;
const owner = globalVars.github_username;
const repo = globalVars.github_repository;
console.log(`Usuário GitHub: ${owner}`);
console.log(`Repositório GitHub: ${repo}`);

// Obtém o caminho base para os backups (nó "n8n Backups")
const backupNode = $('n8n Backups').first().json;
const basePath = backupNode.stdout;
console.log(`Caminho base para backups: ${basePath}`);

// ===================================================================
// 2. Dados de Entrada para Cada Grupo
// ===================================================================

// (a) Itens n8n para backup (workflows/credentials)
//     - Todos os itens do nó "Convert Files" serão processados para backup.
const convertItemsAll = $('Convert Files').all();
const n8nItems = convertItemsAll;  
console.log(`Total de itens n8n para processar: ${n8nItems.length}`);

// Backup anterior dos itens n8n (ordem compatível)
const n8nBackupItems = $('Get n8n Backups').all();
console.log(`Total de backups n8n obtidos: ${n8nBackupItems.length}`);

// (b) Itens de stacks para backup (do Portainer)
//     - Itens vêm do nó "Get Stacks" (cada um com stackName e file.StackFileContent)
const stackItems = $('Get Stacks').all();
console.log(`Total de stacks para processar: ${stackItems.length}`);

// Backup anterior das stacks (cada item com json.data)
const stackBackupItems = $('Get Stacks Backups').all();
console.log(`Total de backups de stacks obtidos: ${stackBackupItems.length}`);

// (c) Schema do Directus
//     - O novo conteúdo vem do nó "Get Schema" (json.data, formatado com JSON.stringify)
const schemaNewContent = JSON.stringify($('Get Schema').first().json.data, null, 2);
const schemaFilePath = `${basePath}/schema.json`;
const schemaCommitMessage = "Backup schema";
// Obtém o backup anterior do schema do nó "Get Schema Backup"
let schemaBackup = null;
try {
  schemaBackup = $('Get Schema Backup').first().json.data;
  console.log("Backup anterior do schema obtido.");
} catch (e) {
  console.log("Nenhum backup anterior do schema encontrado.");
}

// (d) Token de acesso do GitHub (usa o primeiro item com data.type === 'githubApi')
const githubItem = convertItemsAll.find(item => item.json.data.type === 'githubApi');
if (!githubItem) {
  throw new Error("Nenhum item com type 'githubApi' foi encontrado em Convert Files.");
}
const accessToken = githubItem.json.data.data.accessToken;
console.log("Token de acesso do GitHub obtido com sucesso.");

// ===================================================================
// 3. Monta um Array Unificado de Itens para Processamento
// ===================================================================
let unifiedItems = [];

// Processa os itens n8n
n8nItems.forEach((item, index) => {
  const fileData = item.json.data;
  let fileName = fileData.name;
  if (!fileName.endsWith('.json')) {
    fileName += '.json';
  }
  // Define a pasta: "workflows" se existir a key "nodes", senão "credentials"
  const folderType = (fileData && ('nodes' in fileData)) ? 'workflows' : 'credentials';
  const filePath = `${basePath}/${folderType}/${fileName}`;
  const newContent = JSON.stringify(fileData, null, 2);
  const commitMessage = `Backup ${fileData.type}`;
  let backupContent = null;
  let existingSha = null;
  if (n8nBackupItems[index] && n8nBackupItems[index].json && typeof n8nBackupItems[index].json.data !== 'undefined') {
    backupContent = n8nBackupItems[index].json.data;
    existingSha = n8nBackupItems[index].json.sha;
  }
  unifiedItems.push({
    group: "n8n",
    identifier: fileName,
    filePath: filePath,
    newContent: newContent,
    commitMessage: commitMessage,
    backupContent: backupContent,
    existingSha: existingSha
  });
});

// Processa os itens de stacks
stackItems.forEach((item, index) => {
  const stackName = item.json.stackName;
  const filePath = `${basePath}/stacks/${stackName}.yaml`;
  const newContent = item.json.file.StackFileContent;
  const commitMessage = `Backup stack: ${stackName}`;
  let backupContent = null;
  if (stackBackupItems[index] && stackBackupItems[index].json && typeof stackBackupItems[index].json.data !== 'undefined') {
    backupContent = stackBackupItems[index].json.data;
  }
  unifiedItems.push({
    group: "stack",
    identifier: stackName,
    filePath: filePath,
    newContent: newContent,
    commitMessage: commitMessage,
    backupContent: backupContent,
    existingSha: null
  });
});

// Adiciona o item de schema
unifiedItems.push({
  group: "schema",
  identifier: "schema.json",
  filePath: schemaFilePath,
  newContent: schemaNewContent,
  commitMessage: schemaCommitMessage,
  backupContent: schemaBackup,
  existingSha: null
});

console.log(`Total de itens unificados para backup: ${unifiedItems.length}`);

// ===================================================================
// 4. Função Auxiliar para Requisições HTTP (axios)
// ===================================================================
async function makeRequest(method, url, payload) {
  console.log(`Iniciando requisição ${method.toUpperCase()} para ${url}`);
  try {
    const response = await axios({
      method,
      url,
      data: payload,
      headers: {
        Authorization: `Bearer ${accessToken}`,
        Accept: 'application/vnd.github.v3+json',
        'Content-Type': 'application/json'
      }
    });
    console.log(`Requisição ${method.toUpperCase()} concluída com sucesso.`);
    return response;
  } catch (error) {
    const errorDetails = error.response ? error.response.data : '';
    console.error(`Erro na requisição ${method.toUpperCase()} para ${url}: ${error.message}`, errorDetails);
    throw error;
  }
}

// ===================================================================
// 5. Processa Cada Item (Criar/Atualizar Arquivo no GitHub)
// ===================================================================
const results = [];

for (let i = 0; i < unifiedItems.length; i++) {
  const item = unifiedItems[i];
  console.log(`\n--- Processando [${item.group}] '${item.identifier}' ---`);

  // Normaliza os conteúdos para comparação (remove espaços extras no início/fim)
  const normalizedNewContent = item.newContent.trim();
  const normalizedBackupContent = (typeof item.backupContent === 'string')
    ? item.backupContent.trim()
    : null;

  // Prepara informações avançadas para logs
  const advancedInfo = {
    group: item.group,
    identifier: item.identifier,
    filePath: item.filePath,
    commitMessage: item.commitMessage,
    newContent: item.newContent,
    normalizedNewContent: normalizedNewContent,
    backupContent: item.backupContent,
    normalizedBackupContent: normalizedBackupContent,
    primaryAction: null,
    payload: {}
  };

  // Se o novo conteúdo for igual ao backup (após normalização), nenhuma operação é realizada.
  if (normalizedBackupContent === normalizedNewContent) {
    console.log(`Arquivo '${item.identifier}' já está atualizado. Operação ignorada.`);
    let resultObj = {
      identifier: item.identifier,
      filePath: item.filePath,
      action: 'none',
      status: 'up-to-date'
    };
    if (advancedLogsEnabled) {
      resultObj.advancedLogs = advancedInfo;
    }
    results.push(resultObj);
    continue;
  }

  // Define a operação com base no backup: se backupContent é null, operação "create", senão "update".
  const primaryAction = (normalizedBackupContent === null) ? 'create' : 'update';
  advancedInfo.primaryAction = primaryAction;

  const url = `https://api.github.com/repos/${owner}/${repo}/contents/${encodeURIComponent(item.filePath)}`;
  console.log(`URL da API do GitHub: ${url}`);

  // Prepara o payload
  const encodedContent = Buffer.from(item.newContent).toString('base64');
  const payload = {
    message: item.commitMessage,
    content: encodedContent,
    branch: 'main',
    committer: {
      name: 'n8n Backup Bot',
      email: 'backup@example.com'
    }
  };
  advancedInfo.payload = payload;

  // Se a operação for update e não tivermos SHA, obtém-o via GET
  if (primaryAction === 'update') {
    if (item.existingSha) {
      payload.sha = item.existingSha;
    } else {
      try {
        console.log(`Obtendo SHA para atualização do arquivo '${item.identifier}'...`);
        const getResponse = await axios.get(url, {
          headers: {
            Authorization: `Bearer ${accessToken}`,
            Accept: 'application/vnd.github.v3+json'
          }
        });
        payload.sha = getResponse.data.sha;
        console.log(`SHA obtido: ${payload.sha}`);
      } catch (error) {
        console.error(`Erro ao obter SHA para atualização do arquivo '${item.identifier}': ${error.message}`);
        let resultObj = {
          identifier: item.identifier,
          filePath: item.filePath,
          status: 'failed',
          error: error.toString(),
          details: error.response ? error.response.data : null
        };
        if (advancedLogsEnabled) {
          resultObj.advancedLogs = advancedInfo;
        }
        results.push(resultObj);
        continue;
      }
    }
  }

  let operationPerformed = false;
  let actionLog = '';

  // Tenta a operação primária
  try {
    console.log(`Tentando ${primaryAction === 'create' ? 'criar' : 'atualizar'} o arquivo '${item.identifier}'...`);
    const response = await makeRequest('put', url, payload);
    actionLog = primaryAction === 'create' ? 'created' : 'updated';
    console.log(`Arquivo '${item.identifier}' ${actionLog} com sucesso.`);
    let resultObj = {
      identifier: item.identifier,
      filePath: item.filePath,
      action: actionLog,
      status: 'success',
      data: response.data
    };
    if (advancedLogsEnabled) {
      resultObj.advancedLogs = advancedInfo;
    }
    results.push(resultObj);
    operationPerformed = true;
  } catch (error) {
    console.error(`Erro na tentativa primária (${primaryAction}) para o arquivo '${item.identifier}': ${error.message}`, error.response ? error.response.data : '');
    // Tenta a operação inversa (fallback)
    try {
      console.log(`Tentando ação alternativa para o arquivo '${item.identifier}'...`);
      const getResponse = await axios.get(url, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
          Accept: 'application/vnd.github.v3+json'
        }
      });
      const fetchedSha = getResponse.data.sha;
      console.log(`SHA obtido na tentativa alternativa: ${fetchedSha}`);
      if (primaryAction === 'create') {
        payload.sha = fetchedSha;
        actionLog = 'fallback: updated (primary create error)';
      } else {
        delete payload.sha;
        actionLog = 'fallback: created (primary update error)';
      }
      const response = await makeRequest('put', url, payload);
      console.log(`Arquivo '${item.identifier}' ${actionLog} com sucesso.`);
      let resultObj = {
        identifier: item.identifier,
        filePath: item.filePath,
        action: actionLog,
        status: 'success',
        data: response.data
      };
      if (advancedLogsEnabled) {
        resultObj.advancedLogs = advancedInfo;
      }
      results.push(resultObj);
      operationPerformed = true;
    } catch (error2) {
      console.error(`Falha na tentativa alternativa para o arquivo '${item.identifier}': ${error2.message}`, error2.response ? error2.response.data : '');
      let resultObj = {
        identifier: item.identifier,
        filePath: item.filePath,
        status: 'failed',
        error: error2.toString(),
        details: error2.response ? error2.response.data : null
      };
      if (advancedLogsEnabled) {
        resultObj.advancedLogs = advancedInfo;
      }
      results.push(resultObj);
      operationPerformed = true;
    }
  }

  if (operationPerformed) {
    await delay(1000);
  }
}

// Retorna os resultados no formato esperado pelo n8n
return results.map(result => ({ json: result }));

/**
 * Código para recuperar todas as stacks do Portainer e os respectivos arquivos (file) de cada stack.
 * 
 * Lógica:
 * 1. Obtém as credenciais do Portainer a partir do nó "Get Global Vars" e valida que não sejam nulas.
 * 2. Realiza login no Portainer para obter o token JWT.
 * 3. Obtém todas as stacks do Portainer.
 * 4. Para cada stack, obtém o arquivo associado (file).
 * 5. Se ocorrer algum erro, os logs detalhados serão retornados.
 * 6. Se tudo ocorrer normalmente, retorna apenas os resultados dos arquivos.
 * 
 * Os resultados são retornados no formato esperado pelo n8n: cada item encapsulado em { json: { ... } }.
 */

const axios = require('axios');

// Função de delay (aguarda ms milissegundos)
const delay = ms => new Promise(resolve => setTimeout(resolve, ms));

// ----------------------------------------------------------------------
// 1. Obter parâmetros do Portainer
// ----------------------------------------------------------------------
const globalVars = $('Get Global Vars').first().json;
const portainerUsername = globalVars.portainer_username;
const portainerPassword = globalVars.portainer_password;

if (!portainerUsername || !portainerPassword) {
  const errorMsg = `Parâmetros inválidos: portainer_username='${portainerUsername}', portainer_password='${portainerPassword}'`;
  console.error(errorMsg);
  throw new Error(errorMsg);
}

console.log(`Parâmetros do Portainer: username='${portainerUsername}', password='${portainerPassword ? '***' : 'null'}`);

// URL base do Portainer (substituindo 'portainer' pelo valor da variável global)
const portainerUrl = `http://${globalVars.portainer_service}:9000`;

// ----------------------------------------------------------------------
// 2. Função para logar no Portainer e obter um token JWT
// ----------------------------------------------------------------------
async function loginPortainer() {
  const url = `${portainerUrl}/api/auth`;
  console.log(`Realizando login no Portainer em: ${url}`);
  try {
    const response = await axios.post(url, {
      Username: portainerUsername,
      Password: portainerPassword
    });
    console.log("Login realizado com sucesso. Token JWT obtido.");
    return response.data.jwt;
  } catch (error) {
    const errDetails = error.response ? error.response.data : error.message;
    console.error("Erro ao realizar login no Portainer:", errDetails);
    throw new Error(`Erro no login no Portainer: ${JSON.stringify(errDetails)}`);
  }
}

// ----------------------------------------------------------------------
// 3. Função para obter todas as stacks do Portainer
// ----------------------------------------------------------------------
async function getStacks(jwt) {
  const url = `${portainerUrl}/api/stacks`;
  console.log(`Obtendo stacks do Portainer em: ${url}`);
  try {
    const response = await axios.get(url, {
      headers: {
        Authorization: `Bearer ${jwt}`
      }
    });
    console.log(`Total de stacks obtidas: ${response.data.length}`);
    return response.data;
  } catch (error) {
    const errDetails = error.response ? error.response.data : error.message;
    console.error("Erro ao obter stacks:", errDetails);
    throw new Error(`Erro ao obter stacks: ${JSON.stringify(errDetails)}`);
  }
}

// ----------------------------------------------------------------------
// 4. Função para obter o arquivo (file) de uma stack específica
// ----------------------------------------------------------------------
async function getStackFile(jwt, stackId) {
  const url = `${portainerUrl}/api/stacks/${stackId}/file`;
  console.log(`Obtendo arquivo da stack ${stackId} em: ${url}`);
  try {
    const response = await axios.get(url, {
      headers: {
        Authorization: `Bearer ${jwt}`
      }
    });
    console.log(`Arquivo da stack ${stackId} obtido com sucesso.`);
    return response.data;
  } catch (error) {
    const errDetails = error.response ? error.response.data : error.message;
    console.error(`Erro ao obter arquivo da stack ${stackId}:`, errDetails);
    throw new Error(`Erro ao obter arquivo da stack ${stackId}: ${JSON.stringify(errDetails)}`);
  }
}

// ----------------------------------------------------------------------
// 5. Função principal
// ----------------------------------------------------------------------
async function main() {
  const results = [];
  const logs = [];

  try {
    // Realiza login e obtém o token JWT
    const jwt = await loginPortainer();
    logs.push({ step: "login", message: "Login realizado com sucesso.", jwt });

    // Obtém todas as stacks
    const stacks = await getStacks(jwt);
    logs.push({ step: "getStacks", message: "Stacks obtidas com sucesso.", total: stacks.length });

    // Para cada stack, obtém o arquivo associado
    for (const stack of stacks) {
      console.log(`Processando stack - ID: ${stack.Id}, Nome: ${stack.Name}`);
      try {
        const file = await getStackFile(jwt, stack.Id);
        logs.push({ step: "getStackFile", stackId: stack.Id, message: "Arquivo obtido com sucesso." });
        results.push({
          stackId: stack.Id,
          stackName: stack.Name,
          file: file
        });
      } catch (error) {
        logs.push({ step: "getStackFile", stackId: stack.Id, message: "Erro ao obter arquivo.", error: error.toString() });
        results.push({
          stackId: stack.Id,
          stackName: stack.Name,
          error: error.toString()
        });
      }
      await delay(1000);
    }

    // Se tudo ocorrer normalmente, retorna apenas os resultados
    return results.map(item => ({ json: item }));

  } catch (error) {
    console.error("Erro no processamento principal:", error.toString());
    // Em caso de erro, retorna os logs detalhados junto com a mensagem de erro
    return [{ json: { error: error.toString(), logs } }];
  }
}

return main();

/**
 * Este código recupera os backups do repositório GitHub,
 * cujos arquivos foram salvos na pasta definida em $('n8n Backups').first().json.stdout.
 * Ele retorna:
 *  - credentials: um array com os conteúdos JSON de cada arquivo na pasta "credentials"
 *  - workflows: um array com os conteúdos JSON de cada arquivo na pasta "workflows"
 *  - schema: o conteúdo JSON do arquivo "schema.json"
 *
 * As requisições são autenticadas usando o token obtido do nó "Convert Files" (item com data.type === 'githubApi').
 */

// Importa o axios para as requisições HTTP
const axios = require('axios');

// Configurações iniciais
const basePath = $('n8n Backups').first().json.stdout;
const globalVars = $('Get Global Vars').first().json;
const owner = globalVars.github_username;
const repo = globalVars.github_repository;

// Obtém o token de acesso do GitHub do nó "Convert Files"
const convertItems = $('Convert Files').all();
const githubItem = convertItems.find(item => item.json.data.type === 'githubApi');
if (!githubItem) {
  throw new Error("Nenhum item com type 'githubApi' foi encontrado em Convert Files.");
}
const accessToken = githubItem.json.data.data.accessToken;

// Função para fazer GET em um caminho do repositório e retornar o objeto (ou array) JSON com o conteúdo do arquivo
async function getFileContent(path) {
  const url = `https://api.github.com/repos/${owner}/${repo}/contents/${encodeURIComponent(path)}`;
  const response = await axios.get(url, {
    headers: {
      Authorization: `Bearer ${accessToken}`,
      Accept: 'application/vnd.github.v3+json'
    }
  });
  // A API retorna um objeto com a propriedade "content" (base64 encoded)
  const contentBase64 = response.data.content;
  // Remove quebras de linha se existirem e decodifica
  const contentStr = Buffer.from(contentBase64.replace(/\n/g, ''), 'base64').toString('utf8');
  return contentStr;
}

// Função para listar os arquivos de um diretório e retornar um array de caminhos
async function listDirectoryFiles(path) {
  const url = `https://api.github.com/repos/${owner}/${repo}/contents/${encodeURIComponent(path)}`;
  const response = await axios.get(url, {
    headers: {
      Authorization: `Bearer ${accessToken}`,
      Accept: 'application/vnd.github.v3+json'
    }
  });
  // A resposta é um array de objetos, cada um representando um arquivo (ou subdiretório)
  // Filtramos apenas os arquivos (type === 'file')
  return response.data.filter(item => item.type === 'file');
}

// Função que obtém e parseia todos os arquivos de um diretório (esperando JSON)
async function getDirectoryJSON(path) {
  const files = await listDirectoryFiles(path);
  const results = [];
  for (let file of files) {
    try {
      const contentStr = await getFileContent(file.path);
      // Tenta fazer o parse; se der erro, guarda o texto bruto
      const parsed = JSON.parse(contentStr);
      results.push(parsed);
    } catch (e) {
      // Se não for JSON, podemos armazenar o conteúdo bruto ou tratar de outra forma
      results.push({ error: "Erro no parse JSON", raw: contentStr });
    }
  }
  return results;
}

// Função principal para recuperar os backups e parseá-los
async function main() {
  // Monta os caminhos:
  // As credenciais estarão na pasta: {basePath}/credentials
  // Os workflows estarão na pasta: {basePath}/workflows
  // O schema estará em: {basePath}/schema.json
  const credentialsPath = `${basePath}/credentials`;
  const workflowsPath = `${basePath}/workflows`;
  const schemaPath = `${basePath}/schema.json`;

  // Obtém os arquivos e parseia-os
  let credentials = [];
  let workflows = [];
  let schema = null;

  try {
    credentials = await getDirectoryJSON(credentialsPath);
  } catch (e) {
    console.error("Erro ao recuperar arquivos de credentials:", e);
  }

  try {
    workflows = await getDirectoryJSON(workflowsPath);
  } catch (e) {
    console.error("Erro ao recuperar arquivos de workflows:", e);
  }

  try {
    const schemaContentStr = await getFileContent(schemaPath);
    schema = JSON.parse(schemaContentStr);
  } catch (e) {
    console.error("Erro ao recuperar ou parsear schema:", e);
    schema = { error: "Erro ao recuperar ou parsear schema", raw: null };
  }

  // Retorna os resultados já parseados
  return [{ json: { credentials, workflows, schema } }];
}

// Executa a função principal e retorna o resultado
return main();

/**
 * Código final para:
 *   - Criar as credenciais e workflows no n8n a partir dos backups, somente se eles ainda não existirem.
 *   - Atualizar o schema do Directus aplicando o diff do backup.
 *
 * Requisitos:
 * - O nó "Get Backups" deve retornar um JSON com a estrutura:
 *     { 
 *       credentials: [ { name, type, data, ... }, ... ],
 *       workflows: [ { name, nodes, connections, ... }, ... ],
 *       schema: { ... }
 *     }
 *
 * - O nó "Convert Files" deve retornar vários itens; dentre eles, o item com data.type === 'n8nApi'
 *   contém o token da n8n API (campo data.data.apiKey) e os demais itens possuem a propriedade data.name,
 *   que representam os itens atuais (credenciais e workflows) do n8n.
 *
 * - O nó "Get Global Vars" possui o token do Directus em directus_token.
 *
 * Endpoints:
 *   - n8n API: http://{n8n_editor_service}:5678/api/v1
 *       * POST /credentials: { name, type, data }
 *       * POST /workflows: { name, nodes, connections, settings }
 *   - Directus:
 *       * Diff: POST http://{directus_service}:8055/schema/diff?force=true  
 *              (payload: backup schema; retorno: { data: { hash, diff, ... } })
 *       * Apply: POST http://{directus_service}:8055/schema/apply  
 *              (payload: o objeto retornado em data do diff)
 *
 * Logs detalhados são gerados para cada etapa e um delay de 1 segundo é aplicado entre operações.
 *
 * A variável interna advancedLogsEnabled controla se a chave advancedLogs será incluída nos resultados.
 */

const axios = require('axios');

// ----------------------------------------------------------------------
// Configurações Gerais (hardcoded)
// ----------------------------------------------------------------------
const advancedLogsEnabled = false; // Altere para true para incluir logs avançados

// Função de delay
const delay = ms => new Promise(resolve => setTimeout(resolve, ms));

// ----------------------------------------------------------------------
// 1. Obter dados e tokens
// ----------------------------------------------------------------------

// Backup dos itens a partir do nó "Get Backups"
const backupData = $('Get Backups').first().json; 
// Estrutura esperada: { credentials: [...], workflows: [...], schema: { ... } }

// Itens atuais do n8n (para verificação de nomes) a partir do nó "Convert Files"
const currentItems = $('Convert Files').all();  
// Cada item tem em item.json.data.name

// Token da n8n API: busca o item cujo data.type === 'n8nApi'
const n8nApiItem = currentItems.find(item => item.json.data.type === 'n8nApi');
if (!n8nApiItem) {
  throw new Error("Nenhum item com type 'n8nApi' foi encontrado em Convert Files.");
}
const n8nApiToken = n8nApiItem.json.data.data.apiKey;
console.log(`Token da n8n API obtido: ${n8nApiToken}`);

// Token do Directus: obtido do nó "Get Global Vars"
const globalVars = $('Get Global Vars').first().json;
const directusToken = globalVars.directus_token;
console.log(`Token do Directus obtido: ${directusToken}`);

// Endpoints base (ajustados conforme as variáveis dos serviços)
const n8nBaseUrl = `http://${globalVars.n8n_editor_service}:5678/api/v1`;
const directusBaseUrl = `http://${globalVars.directus_service}:8055`;

// ----------------------------------------------------------------------
// 2. Funções auxiliares para chamadas HTTP
// ----------------------------------------------------------------------

// Função para criar uma credencial no n8n via POST
async function createN8nCredential(payload) {
  const url = `${n8nBaseUrl}/credentials`;
  const response = await axios.post(url, payload, { 
    headers: { 
      "X-N8N-API-KEY": n8nApiToken, 
      "Content-Type": "application/json" 
    } 
  });
  return response.data;
}

// Função para criar um workflow no n8n via POST
async function createN8nWorkflow(payload) {
  const url = `${n8nBaseUrl}/workflows`;
  const response = await axios.post(url, payload, { 
    headers: { 
      "X-N8N-API-KEY": n8nApiToken, 
      "Content-Type": "application/json" 
    } 
  });
  return response.data;
}

// Função para obter o diff do schema do Directus via POST
async function diffDirectusSchema(schemaBackup) {
  const url = `${directusBaseUrl}/schema/diff?force=true`;
  const response = await axios.post(url, schemaBackup, { 
    headers: { 
      Authorization: `Bearer ${directusToken}`, 
      "Content-Type": "application/json" 
    } 
  });
  // Retorna o objeto diff (que estará na propriedade "data" do response)
  return response.data;
}

// Função para aplicar o diff no schema do Directus via POST
async function applyDirectusSchema(diffData) {
  const url = `${directusBaseUrl}/schema/apply`;
  const response = await axios.post(url, diffData, { 
    headers: { 
      Authorization: `Bearer ${directusToken}`, 
      "Content-Type": "application/json" 
    } 
  });
  return response.data;
}

// ----------------------------------------------------------------------
// 3. Função principal para recriar itens e atualizar o schema
// ----------------------------------------------------------------------
async function main() {
  const logs = [];

  // 3.1 Criar Credenciais
  const backupCredentials = backupData.credentials || [];
  for (let cred of backupCredentials) {
    // Verifica se já existe uma credencial com o mesmo nome entre os itens atuais (usando data.name)
    const exists = currentItems.find(item => item.json.data.name === cred.name);
    if (exists) {
      logs.push({ message: `Credencial '${cred.name}' já existe. Ignorada.` });
      continue;
    }
    // Prepara o payload para criação da credencial.
    // O endpoint espera que o campo "data" seja um objeto.
    const payload = {
      name: cred.name,
      type: cred.type,
      data: cred.data
    };
    try {
      logs.push({ message: `Criando credencial '${cred.name}'...`, payload });
      const result = await createN8nCredential(payload);
      logs.push({ message: `Credencial '${cred.name}' criada com sucesso.`, result });
    } catch (error) {
      logs.push({ 
        message: `Erro ao criar credencial '${cred.name}'.`, 
        error: error.toString(), 
        details: error.response ? error.response.data : null 
      });
    }
    await delay(1000);
  }

  // 3.2 Criar Workflows
  const backupWorkflows = backupData.workflows || [];
  for (let wf of backupWorkflows) {
    // Verifica se já existe um workflow com o mesmo nome entre os itens atuais
    const exists = currentItems.find(item => item.json.data.name === wf.name);
    if (exists) {
      logs.push({ message: `Workflow '${wf.name}' já existe. Ignorado.` });
      continue;
    }
    // Prepara o payload para criação do workflow.
    // **Importante:** Incluímos "settings": {} pois é uma propriedade obrigatória.
    const payload = {
      name: wf.name,
      nodes: wf.nodes,
      connections: wf.connections,
      settings: {}
    };
    try {
      logs.push({ message: `Criando workflow '${wf.name}'...`, payload });
      const result = await createN8nWorkflow(payload);
      logs.push({ message: `Workflow '${wf.name}' criado com sucesso.`, result });
    } catch (error) {
      logs.push({ 
        message: `Erro ao criar workflow '${wf.name}'.`, 
        error: error.toString(), 
        details: error.response ? error.response.data : null 
      });
    }
    await delay(1000);
  }

  // 3.3 Atualizar Schema do Directus
  let diffData = null;
  let attempts = 0;
  const maxAttempts = 3;
  while (attempts < maxAttempts) {
    try {
      logs.push({ message: "Obtendo diff do schema do Directus...", backupSchema: backupData.schema });
      diffData = await diffDirectusSchema(backupData.schema);
      logs.push({ message: "Diff do schema obtido com sucesso.", diffData });
      break;
    } catch (error) {
      attempts++;
      logs.push({ 
        message: `Erro ao obter diff do schema (tentativa ${attempts} de ${maxAttempts}).`, 
        error: error.toString(), 
        details: error.response ? error.response.data : null 
      });
      await delay(2000);
    }
  }

  if (diffData && diffData.data) {
    try {
      logs.push({ message: "Aplicando diff no schema do Directus...", diffData: diffData.data });
      const updatedSchema = await applyDirectusSchema(diffData.data);
      logs.push({ message: "Schema do Directus atualizado com sucesso.", updatedSchema });
    } catch (error) {
      logs.push({ 
        message: "Erro ao aplicar diff no schema do Directus.", 
        error: error.toString(), 
        details: error.response ? error.response.data : null 
      });
    }
  } else {
    logs.push({ message: "Nenhum diff obtido; o schema não foi alterado." });
  }

  return logs;
}

return main().then(logs => logs.map(log => ({ json: log })));

