const inputData = $input.all();
const visits = inputData[0]?.json?.visits || [];
const jobId = inputData[0]?.json?.jobId;
const jobNumber = inputData[0]?.json?.jobNumber;

return visits.map(visit => ({
  json: {
    ...visit,
    jobId: jobId,
    jobNumber: jobNumber
  }
}));

// Get pagination info
const hasNextPage = $node["pagination1"].json.pagination.hasNextPage;

// Return different outputs based on pagination status
if (hasNextPage) {
  return [{ json: { continue: true, endCursor: $node["pagination1"].json.pagination.endCursor } }];
} else {
  return [{ json: { continue: false } }];
}

const inputData = $input.all();
const data = inputData[0]?.json || {};
return data.assignedUsers.edges.map(user => ({
    json: {
      jobber_id: user.node.id,
      visit_jobber_id: data.id,
      firstname: user.node.name.first,
      lastname: user.node.name.last,
      full_name: `${user.node.name.first} ${user.node.name.last}`
    }
}));

const inputData = $input.all();
const data = inputData[0]?.json || {};
return (data.lineItems?.edges || []).map(edge => ({ 
  json: { 
    visit_id: data.id, 
    id: edge.node.id, 
    name: edge.node.name, 
    quantity: edge.node.quantity, 
    totalPrice: edge.node.totalPrice 
  } 
}));

const inputData = $input.all();

let resultArray = [];

inputData.forEach(item => {
  if (item.json && item.json.propertyName) {
    item.json.propertyName.forEach(innerItem => {
      let parsedItem = JSON.parse(innerItem);
      resultArray.push({ json: parsedItem.data });
    });
  }
});

return resultArray;

